mapper.select:
select ano,aname,atype,aarea,aimage
	from (
	select @rownum:=@rownum+1 as rownum, ano,aname,atype,aarea,aimage
	from (select @rownum:=0) as rownum
	,academys
	
	where aname like concat ('%',#{keyword},'%')
	
	order by ano desc
	) academys

where rownum > (#{pageNum}-1) * #{amount} and rownum <= #{pageNum} * #{amount}
