mapper.select:
select mno,mtitle,msubtitle,mtype,mregdate,mwriter
from (
	select @rownum:=@rownum+1 as rownum, mno,mtitle,msubtitle,mtype,mregdate,mwriter
	from (select @rownum:=0) as rownum 
		, magazines
	
	where mtitle like concat ('%',#{keyword},'%')
	
	order by mno desc
) magazines

where rownum > (#{pageNum}-1) * #{amount} and rownum <= #{pageNum} * #{amount}
